package com.java.demo.intro;

public class EvenOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         int iValue = 15;
         
         if((iValue % 2 ) == 0)
         {
        	  System.out.println(" Even Number");
          }
         else
         {
        	 System.out.println(" Odd Number"); 
         }
         
	}

}
